package introduce.myteam.controllerimport introduce.myteam.service.UserServiceimport org.springframework.web.bind.annotation.GetMappingimport org.springframework.web.bind.annotation.PathVariableimport org.springframework.web.bind.annotation.RequestMappingimport org.springframework.web.bind.annotation.RestController@RestController@RequestMapping("/users")class UserController(val userService: UserService) {    @GetMapping("/")    fun users() {        val users = userService.findAll()        println(            users.forEach {                println(                    """            ${it.id}             ${it.name}             ${it.description}             ${it.blogUrl}             ${it.githubUrl}             ${it.profileImg}         """,                )            },        )    }    @GetMapping("/{userId}")    fun userDetail(@PathVariable userId: Long): String {        val user = userService.findUser(userId)        println(            """            ${user.id}             ${user.name}             ${user.description}             ${user.blogUrl}             ${user.githubUrl}             ${user.profileImg}         """,        )        return "/detail.html"    }}