package introduce.myteam.domainimport jakarta.persistence.Columnimport jakarta.persistence.Entityimport jakarta.persistence.GeneratedValueimport jakarta.persistence.GenerationTypeimport jakarta.persistence.Idimport jakarta.persistence.Table@Entity@Table(name = "user")class User(    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "user_id")    val id: Long,    @Column(nullable = false)    val name: String,    @Column(nullable = false)    val description: String,    @Column(name = "profile_img")    val profileImg: String,    @Column(name = "github_url")    val githubUrl: String,    @Column(name = "blog_url")    val blogUrl: String,)