package introduce.myteam.domain.todosimport jakarta.persistence.*@Entity@Table(name = "todos")class Todo {    constructor(userId: Long, value: String) {        this.userId = userId        this.value = value    }    constructor(id: Long, userId: Long, value: String) {        this.id = id        this.userId = userId        this.value = value    }    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "todo_id")    var id: Long = 0        protected set    @Column(name = "todo_val")    var value: String        protected set    @Column(name = "user_id")    var userId: Long        protected set}