package introduce.myteam.domain.todos.controllerimport introduce.myteam.domain.todos.dto.CreateTodoRequestimport introduce.myteam.domain.todos.dto.TodoResponseimport introduce.myteam.domain.todos.service.TodoServiceimport org.springframework.stereotype.Controllerimport org.springframework.web.bind.annotation.*@Controller@RequestMapping("/todos")class TodoController(private val todoService: TodoService) {    @PostMapping("/")    fun createTodo(request: CreateTodoRequest): String {        val userId = request.userId        todoService.save(request)        return "redirect:/" + userId    }    @ResponseBody    @GetMapping("/{userId}")    fun findAllTodos(@PathVariable userId: Long): List<TodoResponse> {        return todoService.findByUserId(userId)    }    //TODO: 이건 구현 안 할듯?    @PatchMapping("/{userId}")    fun editTodo(@PathVariable userId: Long): String {        return "redirect:/"    }    @DeleteMapping("/{todoId}")    fun removeTodo(@PathVariable todoId: Long): String {        val userId = todoService.remove(todoId)        return "redirect:/" + userId    }}