package introduce.myteam.domain.todos.serviceimport introduce.myteam.domain.todos.dto.CreateTodoRequestimport introduce.myteam.domain.todos.dto.TodoResponseimport introduce.myteam.domain.todos.repository.TodoRepositoryimport org.springframework.data.repository.findByIdOrNullimport org.springframework.stereotype.Service@Serviceclass TodoService(private val todoRepository: TodoRepository) {    fun save(request: CreateTodoRequest) {        todoRepository.save(request.toEntity())    }    fun findByUserId(userId: Long): List<TodoResponse> {        return todoRepository.findByUserId(userId)            .map(TodoResponse::from)    }    fun findAll(): List<TodoResponse> {        return todoRepository.findAll()            .map(TodoResponse::from)    }    fun remove(todoId: Long): Long {        val todo = todoRepository.findByIdOrNull(todoId)            ?: throw NoSuchElementException()        todoRepository.delete(todo)        return todo.userId    }}