package introduce.myteam.domain.userimport jakarta.persistence.*@Table(name = "users")@Entityclass User(name: String, description: String, profileImg: String, githubUrl: String, blogUrl: String) {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "user_id")    var id: Long = 0        protected set    @Column(nullable = false)    var name: String = name        protected set    @Column(nullable = false)    var description: String = description        protected set    @Column(name = "profile_img")    var profileImg: String = profileImg        protected set    @Column(name = "github_url")    var githubUrl: String = githubUrl        protected set    @Column(name = "blog_url")    var blogUrl: String = blogUrl        protected set//    @OneToMany//    @JoinColumn(name = "user_id")//    var todos: List<Todo> = ArrayList()//        protected set}