package introduce.myteam.domain.user.controllerimport introduce.myteam.domain.user.dto.UserJoinRequestimport introduce.myteam.domain.user.service.UserServiceimport org.springframework.stereotype.Controllerimport org.springframework.ui.Modelimport org.springframework.web.bind.annotation.*@Controllerclass UserController(private val userService: UserService) {    @PostMapping("/join")    fun join(request: UserJoinRequest): String {        userService.save(request)        return "redirect:/"    }    @GetMapping("/")    fun users(model: Model): String {        val users = userService.findAll()        model.addAttribute("users", users)        return "users"    }    @GetMapping("/{userId}")    fun userDetail(@PathVariable userId: Long, model: Model): String {        val user = userService.findUser(userId)        model.addAttribute("user", user)        return "detail"    }    @DeleteMapping("/remove/{userId}")    fun remove(@PathVariable userId: Long): String {        userService.remove(userId)        return "redirect:/"    }}