package introduce.myteam.domain.user.dtoimport introduce.myteam.domain.user.Userdata class UserResponse(    val id: Long,    val name: String,    val description: String,    val githubUrl: String,    val blogUrl: String,    val profileImg: String,) {    companion object {        fun from(user: User): UserResponse {            return UserResponse(                id = user.id!!,                name = user.name,                description = user.description,                githubUrl = user.githubUrl,                blogUrl = user.blogUrl,                profileImg = user.profileImg            )        }    }}