package introduce.myteam.serviceimport introduce.myteam.domain.Userimport introduce.myteam.repository.UserRepositoryimport org.springframework.data.repository.findByIdOrNullimport org.springframework.stereotype.Service@Serviceclass UserService(val userRepository: UserRepository) {    fun findUser(userId: Long): User {        return userRepository.findByIdOrNull(userId)            ?: throw IllegalArgumentException()    }    fun findAll(): List<User> {        return userRepository.findAll()    }}